import { useState, useEffect, useCallback } from 'react';
import {
    Autocomplete,
    Avatar,
    TableCell,
    TableRow,
    Grid,
    Dialog,
    DialogTitle,
    DialogActions,
    DialogContent,
    Typography,
    Button,
    TextField,
    Chip,
    Box,
    } from '@mui/material';
function Modals (Data) {
    console.log(Data)
    const [opent, setOpent] = useState(false);
    const bitacora= (id) => {
        setOpent(true);
        getBitacoras(id)
      };
      const bitacoraClose = () => {
        setOpent(false);
      };
    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
      };
    return(
        <>
            <div>
                <Button onClick={handleOpen}  variant="contained"><span className='check'>></span>Continuar</Button>
            <Dialog
            maxWidth="md"
            open={opent}
            onClose={bitacoraClose}
            >
            <DialogTitle
            sx={{
            p: 3
            }}
            >
            <Typography variant="h4" gutterBottom>
            <label>BITACORA OPORTUNIDAD No.</label><b>{opor.oport.id}</b>
            </Typography>
            </DialogTitle>
            <form>
            <DialogContent>
            <Grid item xs={12} md={12}>
            <tbody>
            <TableCell>{('#')}</TableCell>
            <TableCell>{('Usuario')}</TableCell>
            <TableCell>{('Fecha')}</TableCell>
            <TableCell>{('Comentario')}</TableCell>
            {bita.map(data => {
            return (
            <TableRow key={data}>
            <TableCell>{data.solinume}</TableCell>
            <TableCell>{data.usuaalia}</TableCell>
            <TableCell>{data.sobifech}</TableCell>
            <div dangerouslySetInnerHTML={{__html: data.sobitext}} className='container'/>
            </TableRow>
            )})}   
            </tbody>
            </Grid>
            </DialogContent>
            <DialogActions
            sx={{
            p: 3
            }}
            >
            <Button  onClick={bitacoraClose} style={{
            background:'#ba000d',
            color:'#fff'
            }}>Cancelar<ClearIcon className='ico'/></Button>
            <Button variant="contained"  onClick={()=>saveBita(opor.oport.id)}>Guardar<SaveIcon className='ico'/></Button>
            </DialogActions>
            </form>
            </Dialog>
        </div>
        </>
    )
}
export default Modals;
